package java1205;

public class char1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char var1 = 'A';
		char var2 = '가';
		
		char c = 65;   		// ASCII 코드값
		char d = 0x0041; 	// 유니코드값
		
		System.out.println(var1);
		System.out.println(var2);
		System.out.println(c);
		System.out.println(d);
		
		
	}

}
